mapper.select:
/*kr.happyjob.study.mngNot.dao.MngNotDao.noticelist*/ select nt.notice_del_yn ,nt.loginID ,nt.notice_no ,nt.notice_title ,nt.notice_date ,nt.notice_cont ,nt.file_no ,fi.file_name ,fi.logic_path ,fi.physic_path ,fi.file_size ,fi.exten ,ui.name from tb_notice nt inner join tb_userinfo ui on ui.loginID = nt.loginID left outer join tb_file fi on fi.file_no = nt.file_no
and nt.notice_del_yn = #{delyn}
and ui.name Like CONCAT('%', #{sname}, '%')
and nt.notice_title LIKE CONCAT('%', #{sname}, '%')
and ( ui.name Like CONCAT('%', #{sname}, '%') or nt.notice_title LIKE CONCAT('%', #{sname}, '%') )
ORDER BY nt.notice_date DESC LIMIT #{pageindex}, #{pageSize}

/*kr.happyjob.study.mngNot.dao.MngNotDao.countnoticelist*/ select count(*) from tb_notice nt inner join tb_userinfo ui on ui.loginID = nt.loginID left outer join tb_file fi on fi.file_no = nt.file_no
and nt.notice_del_yn = #{delyn}
and ui.name Like CONCAT('%', #{sname}, '%')
and nt.notice_title LIKE CONCAT('%', #{sname}, '%')
and ( ui.name Like CONCAT('%', #{sname}, '%') or nt.notice_title LIKE CONCAT('%', #{sname}, '%') )

mapper.insert:

            select ifnull(max(notice_no),0) + 1 from tb_notice

	    insert into tb_notice
	             (
	                notice_no
                  , notice_del_yn
                  , loginID
                  , notice_title
                  , notice_cont
                  , notice_date
                  , file_no
	             )
	    values (
	                #{no} 
	               ,'N'
	               ,#{loginid} 

	               ,#{notice_title} 


	               ,#{file_notice_title} 


	               ,#{notice_cont} 


	               ,#{file_notice_cont} 

	               ,now()

	               ,0


	               ,(select ifnull(max(file_no),0)  from tb_file)

	    )

mapper.update:
	    update tb_notice
	         set notice_title =  #{notice_title}   
                , notice_cont =  #{notice_cont}   
                , notice_date =  now()      
	     where  notice_no =  #{notice_no}

update tb_notice
      set notice_del_yn =  'Y'
  where  notice_no =  #{notice_no}

mapper.insert:

            select ifnull(max(file_no),0) + 1 from tb_file

	    insert into tb_file
	         (
	             file_no
               , file_name
               , logic_path
               , physic_path
               , file_size
               , exten
	         ) values (
	            #{no}
	           ,#{filereturn.file_nm}   
	           ,#{filereturn.vrfile_loc}   
	           ,#{filereturn.file_loc}   
	           ,#{filereturn.file_size}   
	           ,#{filereturn.fileExtension}  
	         )

mapper.delete:
	         delete from tb_file
             where file_no = ( 
                                       select file_no 
                                        from tb_notice
                                      where notice_no = #{notice_no} 
                                   )

mapper.update:
	      update tb_notice
	         set notice_title =  #{file_notice_title}   
                , notice_cont =  #{file_notice_cont}   
                , notice_date =  now()      

                , file_no = (select ifnull(max(file_no),0)  from tb_file)


                , file_no = 0

	     where  notice_no =  #{notice_no}
